import React, { useEffect } from 'react';
import './celestial.css'; // CSS 파일을 import 합니다.

// D3와 Celestial 관련 스크립트 파일들은 public 폴더에 넣고 아래와 같이 static 경로로 접근합니다.
const D3_SCRIPT = '/static/d3.min.js';
const D3_GEO_SCRIPT = '/static/d3.geo.projection.min.js';
const CELESTIAL_SCRIPT = '/static/celestial.min.js';

const loadScript = (src) => {
  return new Promise((resolve) => {
    const script = document.createElement('script');
    script.src = src;
    script.addEventListener('load', () => {
      resolve(true);
    });
    document.body.appendChild(script);
  });
};

const CelestialComponent = () => {
  useEffect(() => {
    const loadAllScripts = async () => {
      await loadScript(D3_SCRIPT);
      await loadScript(D3_GEO_SCRIPT);
      await loadScript(CELESTIAL_SCRIPT);

      const config = {
        // config 내용
      };

      window.Celestial.display(config);
    };

    loadAllScripts();
  }, []);

  return (
    <div>
      <div style={{ overflow: 'hidden' }}>
        <div id='celestial-map'></div>
      </div>
      <div id='celestial-form'></div>
      <footer id='d3-celestial-footer'>
        <p>
          <a href='https://github.com/ofrohn/d3-celestial'>
            <b>D3-Celestial</b>
          </a>{' '}
          released under
          <a href='http://opensource.org/licenses/BSD-3-Clause'>BSD license</a>. Copyright 2015-20
          <a href='http://armchairastronautics.blogspot.com/' rel='author'>
            Olaf Frohn
          </a>
          .
        </p>
      </footer>
    </div>
  );
};

export default CelestialComponent;
